  const [count, setCount] = useState(0);
  useEffect(() => {
    console.log("没有依赖项");
    console.log(count);
  });
  // setTimeout(() => {
  //   setCount(count + 1);
  // }, 500);
  return (
    <div className="App">
      <button onClick={() => setCount(count + 1)}></button>
    </div>
  );
}

export default App;
