import { useEffect, useState } from "react";

function useToggle() {
  const [show, setShow] = useState(true);
  const toggle = () => setShow(!show);
  return {
    show,
    toggle,
  };
}

function App() {
  const { show, toggle } = useToggle();
  return (
    <div className="App">
      {show && <div>this is div</div>}
      <button onClick={toggle}>toggle</button>
    </div>
  );
}

export default App;
